    and u_major=#{major}


    and u_grade=#{grade}


    and u_class=#{classe}

)

select * from user where u_school=#{school}

    and u_academy=#{academy}


    and u_major=#{major}


    and u_grade=#{grade}


    and u_class=#{classe}

and u_id in (select u_id from timecard where to_days(datetime) = to_days(now()))
